class App extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            light: true,
        }

        this.handleLights = this.handleLights.bind(this)
    }    
    /*
    render() {
        return ( 
            <div className = "App">
                <MuiThemeProvider theme={theme}>
                    <AppDrawer/>
                    <SimulationView/>
                </MuiThemeProvider>
            </div>
        )
    }
    */
    render() {
        return ( 
            <div className = "App">
                <AppDrawer/>
                <SimulationView/>
           </div>
        )
    }
}

export default App;
