import React from 'react'
import CodeMirror from 'codemirror'


class CodeView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            lineNumber: false,
            theme: "monokai",
            code: "// Code here"
        }
    }
    render(){
        return(
            
                <CodeMirror 
                    theme={this.state.theme} 
                    code={this.state.code}
                    lineNumber={this.lineNumber}
                /> 
            
        );
    }
}





export default CodeView;
