// change code editor to dark when on
import React from 'react';
import Switch from '@material-ui/core/Switch';

class LightSwitch extends React.Component{
    constructor(props){
        super(props);
        this.state = {
            lightMode: null
        }
    }

    componentWillMount () {
		this.setState( { lightMode: this.props.isChecked } );
	}

    render(){
        return(
            <label>
                <input ref="switch" checked={this.state.lightMode} onChange={this.changeHandler} type="checkbox"/>
            </label>
        );
    }

    changeHandler(){
        this.setState({lightMode: !this.setState.lightMode});
    }
}

export default LightSwitch
