import React from 'react';
import Light from '@material-ui/icons/WbIncandescent';
import PropTypes from 'prop-types';
import LightOff from '@material-ui/icons/WbIncandescentOutlined';
import IconButton from '@material-ui/core/IconButton';

class LightSwitch extends React.Component{
    constructor(props){
    }

    state={
        light: true
    }

    lightOff(){
        this.setState({light: !this.state.light});
        console.log('Light: '+this.state.light);
    }
    render(){
        const {lightOn} = this.state;

        return(
            <IconButton color="inherit">
                <Light
                    onClick={this.lightOff}
                />
            </IconButton>
        );
    };
}

LightSwitch.propTypes = {
    classes: PropTypes.object.isRequired,
};


export default LightSwitch;
