import React from 'react'
import Paper from '@material-ui/core/Paper'
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import Typography from '@material-ui/core/Typography'
import SwipeableViews from 'react-swipeable-views';

class UtilityTabs extends React.Component{
    constructor(props){
        super(props)

        this.state = {
            value: 0,
            consoleOn: true,
        }
        
        this.handleConsole = this.handleConsole.bind(this);
    }

      handleChange = (event, value) => {
          this.setState({
              value: 0,
          });
      };

      handleConsole = () =>{
        this.setState({
          consoleOn: !this.state.consoleOn
        })
        //console.log("This console is : " + this.state.consoleOn)

        if (this.state.consoleOn === true){
            return (
                <Paper component = "div">
                    <Typography variant="h5" component="h3">
                        This is a sheet of paper.
                    </Typography>
                </Paper>
            )
        }
      }
    render(){
        const { value } = this.state;
        return(
            <div className = "test">
                <Paper position = "sticky">
                    <Tabs value={value} onChange={this.handleChange} centered>
                        <Tab label="Console Log" onClick={this.handleConsole}/>
                        <Tab label="Variables" />
                        <Tab label="Statistics" />
                    </Tabs>
                </Paper>
            </div>

        )
    }
}

export default UtilityTabs
