import React, { Component } from 'react';
import AceEditor from 'react-ace';

class Counter extends Component {
    state = { 
        value: 1
    };

    render() {
        return (
            <div>
                <div onChange={() => this.setState({saved: this.state.saved})}>{this.state.value}</div>
                <button onClick={() => { AceEditor.setState({ value: this.state.value + 1 }) }}>Click!</button>
                <AceEditor/>
            </div>
        )
    }
}

export default Counter;
