import React from 'react'
import SwipeableDrawer from '@material-ui/core/SwipeableDrawer';
import AppBar from '@material-ui/core/AppBar/AppBar'
import Divider from '@material-ui/core/Divider'
import List from '@material-ui/core/List'
import ListItem from '@material-ui/core/ListItem'
import ListItemText from '@material-ui/core/ListItemText'
import IconButton from '@material-ui/core/IconButton/IconButton';
import Toolbar from '@material-ui/core/Toolbar/Toolbar'
import MenuIcon from '@material-ui/icons/Menu'
import Typography from '@material-ui/core/Typography'
import { withStyles } from '@material-ui/core/styles'
import AccountCircle from '@material-ui/icons/AccountCircle'
import LightOn from '@material-ui/icons/WbIncandescent'
import LightOff from '@material-ui/icons/WbIncandescentOutlined'
import CssBaseline from '@material-ui/core/CssBaseline'
import Collapse from '@material-ui/core/Collapse'
import ListSubheader from '@material-ui/core/ListSubheader'
import Hidden from '@material-ui/core/Hidden/Hidden'
// give each thing a class name later

const drawerWidth = 240;

const styles = theme => ({
  root: {
    display: 'flex',
  },

  menuButton: {
    marginRight: 20,
    marginLeft: -12,
  },

  appBar: {
    marginLeft: drawerWidth,
    [theme.breakpoints.up('sm')]: {width: `calc(100% - ${drawerWidth}px)`,},
  },

  typography: {
    marginRight: 20,
  },

  accountCircle: {
    marginRight: -12
  },

  lights: {

  },

  appDrawer: {
    [theme.breakpoints.up('sm')]: {
      width: drawerWidth,
      flexShrink: 0,
    },
  }
})

class AppDrawer extends React.Component{

  constructor(props){
    super(props)

    this.state = { 
      light: true,
      drawer: false,
    }
  }

  handleLightChange = () =>{
    this.setState(state => ({light: !this.state.light}))
    //console.log(this.state.light)
  }

  handleMenuOpen = () =>{
    this.setState(state => ({drawer: !this.state.drawer}))
    //console.log(this.state.drawer)
  }

  render(){
    const {classes, theme} = this.props

    const appDrawer = (
      <div>
        <div className={classes.appDrawer}>
          <List>
            <Divider/>
            {['Turing', 'von Neumann'].map((text, index) => (
              <ListItem button key = {text}>
                <ListItemText primary={text}/>
              </ListItem>
            ))}
          </List>
          <Divider/>
          <List>
            {['About Us'].map((text, index) => (
              <ListItem button key = {text}>
                <ListItemText primary={text}/>
              </ListItem>
            ))}
          </List>
        </div>
      </div>
    )

    return(

      <div className = {classes.root}>
        <CssBaseline/>
        <AppBar className={classes.appBar}>
          <Toolbar>
            <IconButton color="inherit" className={classes.menuButton} aria-label="Open Menu" onClick={this.handleMenuOpen}>
              <MenuIcon/>
            </IconButton>
            <Typography variant="h6" color="inherit" noWrap className={classes.typography}>
              FYP18053
            </Typography>
            <IconButton color="inherit" className={classes.lights} onClick={this.handleLightChange} aria-label="Toggle Lights">
              {(!this.state.light)? <LightOff/>: <LightOn/>}
            </IconButton>
            <IconButton color="inherit" className={classes.accountCircle} aria-label="My Account">
              <AccountCircle/>
            </IconButton>
          </Toolbar>
        </AppBar>
        <nav className={classes.appDrawer}>
          <Hidden smUp implementation="css">
            <SwipeableDrawer container={this.props.container} anchor="left" open={this.state.drawer} onClose={this.handleMenuOpen} classes={{paper: classes.drawerPaper}}>
              {appDrawer}
            </SwipeableDrawer>
          </Hidden>
        </nav>
      </div>
    )
  }
}

export default withStyles(styles, {withTheme: true})(AppDrawer)
