import React from 'react'
import LightOn from '@material-ui/icons/WbSunny'
import LightOff from '@material-ui/icons/WbSunnyOutlined'
import IconButton from '@material-ui/core/IconButton/IconButton';
class LightSwitch extends React.Component{
    constructor(props){
        super(props)
        this.state={
            light: this.props.light,
        }
    }

    render(){
        return(
            <div>
                <IconButton color="inherit" onClick={this.props.handleLights} aria-label="Toggle Lights">
                    {(!this.state.light)? <LightOff/>: <LightOn/>}
                </IconButton>
            </div>
          )
      }
}

export default LightSwitch
