import React from 'react'
import './App.css';
import AppDrawer from './components/AppDrawer/AppDrawer'
import { MuiThemeProvider, createMuiTheme } from '@material-ui/core/styles'
import amber from '@material-ui/core/colors/amber'

const theme = createMuiTheme({
    palette: {
        primary: amber,
        type: 'dark',
        secondary: {
            main: '#5c6bc0',
            },
        },
    }
)

class App extends React.Component {
    constructor(props){
        super(props)
        this.state = {
            light: true,
            theme: createMuiTheme({}),

        }
        this.handleLightChange = this.handleLightChange.bind(this)
    }

    handleLightChange = () =>{
        this.setState(state => ({light: !this.state.light}))
        console.log(this.state.light)
        if(this.state.light === true){
            theme.palette.type = 'light'
        }
        else if(this.state.light === false){
            theme.palette.type = 'dark'
        }


        console.log(theme.palette.type)
    }

    render() {
        return ( 
            <div className = "App">
                <MuiThemeProvider theme = {theme}>
                    <AppDrawer handleLightChange={this.handleLightChange} light={this.state.light}/>
                </MuiThemeProvider>
           </div>
        )
    }
}

export default App;
