import React from 'react'
import AceEditor from 'react-ace'
import Brace from 'brace'


class CodeView extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            lineNumber: false,
            theme: "blackboard",
            code: "// Code here"
        }
    }
    render(){
        return(
            <div>
                <AceEditor
                    theme={this.state.theme}
                /> 
            </div>

            
        );
    }
}





export default CodeView;
